mapper.select:
SELECT * FROM base_user


		AND userid like #{userid}


		AND username like #{username}


		AND companyid = #{companyid}


		AND deptid = #{deptid}


		AND dutyid = #{dutyid}

SELECT COUNT(1) FROM base_user


		AND userid like #{userid}


		AND username like #{username}


		AND companyid = #{companyid}


		AND deptid = #{deptid}


		AND dutyid = #{dutyid}

SELECT DISTINCE userid, username, deptid, a.companyid, b.companyname FROM base_user a, base_org b WHERE a.deptid = b.orgid
AND userid like #{userid}
AND username like #{username}
AND a.deptid = #{deptid}
AND a.companyid = #{companyid}
AND userid NOT IN (SELECT userid FROM base_user_role WHERE roleid = #{roleid})
ORDER BY userid

SELECT COUNT(1) FROM (
SELECT DISTINCT userid,username,deptid,a.companyid,b.companyname FROM base_user a, base_org b
WHERE a.deptid = b.orgid

	AND userid like #{userid}


	AND username like #{username}


	AND a.deptid = #{deptid}


	AND a.companyid = #{companyid}


	AND userid NOT IN (SELECT userid FROM base_user_role WHERE roleid = #{roleid})

)
